package Demo;

public class WhileLoopTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=5;
		int i=1;
		
		System.out.println("Multiplication table of"+number);
		
		while(i<=10) {
			System.out.println(number+"x"+i+"="+(number * 1));
			i++;
		}

	}

}
